import React, { useState } from 'react';
import { uploadMeal } from '../services/api';

function MealForm({ onMealUploaded }) {
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10));
  const [mealType, setMealType] = useState('breakfast');
  const [itemsText, setItemsText] = useState(''); // 여러 음식을 쉼표로 구분하여 입력
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setMessage('');
    setLoading(true);

    if (!itemsText.trim()) {
      setError('음식 항목을 하나 이상 입력해주세요.');
      setLoading(false);
      return;
    }

    const mealData = {
      date: date,
      type: mealType,
      items: itemsText.split(',').map(item => item.trim()).filter(item => item),
    };

    try {
      const response = await uploadMeal(mealData);
      setMessage(response.data.message || '식사가 성공적으로 기록되었습니다.');
      setItemsText(''); // 입력 필드 초기화
      if (onMealUploaded) {
        onMealUploaded(response.data.meal, response.data.nutrition);
      }
    } catch (err) {
      setError(err.response?.data?.detail || '식사 기록 중 오류가 발생했습니다.');
      console.error("Error uploading meal:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card">
      <h2 className="card-title">🍚 식사 기록하기</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="date">날짜:</label>
          <input
            type="date"
            id="date"
            value={date}
            onChange={(e) => setDate(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="mealType">식사 종류:</label>
          <select
            id="mealType"
            value={mealType}
            onChange={(e) => setMealType(e.target.value)}
          >
            <option value="breakfast">아침</option>
            <option value="lunch">점심</option>
            <option value="dinner">저녁</option>
            <option value="snack">간식</option>
          </select>
        </div>
        <div>
          <label htmlFor="itemsText">음식 항목 (쉼표로 구분):</label>
          <input
            type="text"
            id="itemsText"
            value={itemsText}
            onChange={(e) => setItemsText(e.target.value)}
            placeholder="예: 현미밥 1공기, 닭가슴살 100g, 김치"
            required
          />
          <small>각 음식 항목을 쉼표(,)로 구분하여 입력하세요.</small>
        </div>
        <button type="submit" disabled={loading}>
          {loading ? '기록 중...' : '식사 기록'}
        </button>
      </form>
      {message && <p style={{ color: 'green' }}>{message}</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default MealForm;
