  const [searchedFoods, setSearchedFoods] = useState([]);
  const [searchLoading, setSearchLoading] = useState(false);
  const [searchError, setSearchError] = useState('');
  const [selectedItemsForMeal, setSelectedItemsForMeal] = useState([]);

  // ÏàòÎèô ÏûÖÎ†• Í¥ÄÎ†® ÏÉÅÌÉú
  const [showManualAddForm, setShowManualAddForm] = useState(false);
  const [manualFoodName, setManualFoodName] = useState('');
  const [manualKcal, setManualKcal] = useState('');
  // Ï∂îÍ∞Ä: ÏàòÎèô ÏûÖÎ†• Ïãú Í∞ÑÎã®Ìïú ÏòÅÏñë Ï†ïÎ≥¥ÎèÑ Ìï®Íªò ÌëúÏãú (Î∞±ÏóîÎìú Ï†ÄÏû•ÏùÄ Ïù¥Î¶ÑÎßå)
  const [manualProtein, setManualProtein] = useState('');
  const [manualCarbs, setManualCarbs] = useState('');
  const [manualFat, setManualFat] = useState('');

  // Î©îÏãúÏßÄ/ÏóêÎü¨/Î°úÎî© ÏÉÅÌÉú (ÏµúÏ¢Ö Ï†úÏ∂úÏö©)
  const [submitMessage, setSubmitMessage] = useState('');
  const [submitError, setSubmitError] = useState('');
  const [submitLoading, setSubmitLoading] = useState(false);

  const handleFoodSearch = async () => {
    if (!currentFoodQuery.trim()) {
      setSearchError('Í≤ÄÏÉâÌï† ÏùåÏãù Ïù¥Î¶ÑÏùÑ ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.');
      return;
    }
    setSearchLoading(true);
    setSearchError('');
    setSearchedFoods([]);
    try {
      const response = await searchFoods(currentFoodQuery);
      setSearchedFoods(response.data.results || []);
      if ((response.data.results || []).length === 0) {
        setSearchError('Í≤ÄÏÉâ Í≤∞Í≥ºÍ∞Ä ÏóÜÏäµÎãàÎã§. ÏßÅÏ†ë Ï∂îÍ∞ÄÌï¥Î≥¥ÏÑ∏Ïöî.');
      }
    } catch (err) {
      setSearchError(err.response?.data?.detail || 'ÏùåÏãù Í≤ÄÏÉâ Ï§ë Ïò§Î•ò Î∞úÏÉù');
      console.error("Error searching foods:", err);
    }
    setSearchLoading(false);
  };

  const handleAddFoodToMeal = (food) => {
    // food Í∞ùÏ≤¥ÏóêÎäî name, nutrition Ï†ïÎ≥¥Í∞Ä ÏûàÏùå
    setSelectedItemsForMeal(prev => [...prev, { name: food.name, source: 'search', nutrition: food.nutrition }]);
  };

  const handleManualFoodAdd = () => {
    if (!manualFoodName.trim() || !manualKcal.trim()) {
        alert('ÏùåÏãù Ïù¥Î¶ÑÍ≥º ÏπºÎ°úÎ¶¨Îäî ÌïÑÏàòÎ°ú ÏûÖÎ†•Ìï¥Ïïº Ìï©ÎãàÎã§.');
        return;
    }
    const newManualFood = {
        name: manualFoodName,
        source: 'manual',
        // ÏàòÎèô ÏûÖÎ†•Îêú ÏòÅÏñë Ï†ïÎ≥¥ÎèÑ Ìï®Íªò Ï†ÄÏû• (ÌîÑÎ°†Ìä∏ÏóîÎìú ÌëúÏãúÏö©)
        nutrition: {
            kcal: parseFloat(manualKcal) || 0,
            protein: parseFloat(manualProtein) || 0,
            fat: parseFloat(manualFat) || 0,
            carbs: parseFloat(manualCarbs) || 0,
        }
    };
    setSelectedItemsForMeal(prev => [...prev, newManualFood]);
    // ÏûÖÎ†• ÌïÑÎìú Ï¥àÍ∏∞Ìôî
    setManualFoodName('');
    setManualKcal('');
    setManualProtein('');
    setManualCarbs('');
    setManualFat('');
    setShowManualAddForm(false);
  };

  const handleRemoveFoodFromMeal = (indexToRemove) => {
    setSelectedItemsForMeal(prev => prev.filter((_, index) => index !== indexToRemove));
  };

  const handleSubmitMeal = async (e) => {
    e.preventDefault();
    setSubmitError('');
    setSubmitMessage('');
    setSubmitLoading(true);

    if (selectedItemsForMeal.length === 0) {
      setSubmitError('ÌïòÎÇò Ïù¥ÏÉÅÏùò ÏùåÏãùÏùÑ Ï∂îÍ∞ÄÌï¥Ï£ºÏÑ∏Ïöî.');
      setSubmitLoading(false);
      return;
    }

    const mealData = {
      date: date,
      type: mealType,
      items: selectedItemsForMeal.map(item => item.name), // Î∞±ÏóîÎìúÏóêÎäî ÏùåÏãù Ïù¥Î¶ÑÎßå Ï†ÑÏÜ°
    };

    try {
      const response = await uploadMeal(mealData);
      setSubmitMessage(response.data.message || 'ÏãùÏÇ¨Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Í∏∞Î°ùÎêòÏóàÏäµÎãàÎã§.');
      setSelectedItemsForMeal([]); // Í∏∞Î°ù ÌõÑ ÏÑ†ÌÉù Î™©Î°ù Ï¥àÍ∏∞Ìôî
      setCurrentFoodQuery('');
      setSearchedFoods([]);
      if (onMealUploaded) {
        onMealUploaded(response.data.meal, response.data.nutrition);
      }
    } catch (err) {
      setSubmitError(err.response?.data?.detail || 'ÏãùÏÇ¨ Í∏∞Î°ù Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      console.error("Error uploading meal:", err);
    } finally {
      setSubmitLoading(false);
    }
  };

  return (
    <div className="card">
      <h2 className="card-title">üçö ÏãùÏÇ¨ Í∏∞Î°ù</h2>
      <form onSubmit={handleSubmitMeal}>
        <div>
          <label htmlFor="date">ÎÇ†Ïßú:</label>
          <input type="date" id="date" value={date} onChange={(e) => setDate(e.target.value)} required />
        </div>
        <div>
          <label htmlFor="mealType">ÏãùÏÇ¨ Ï¢ÖÎ•ò:</label>
          <select id="mealType" value={mealType} onChange={(e) => setMealType(e.target.value)}>
            <option value="breakfast">ÏïÑÏπ®</option>
            <option value="lunch">Ï†êÏã¨</option>
            <option value="dinner">Ï†ÄÎÖÅ</option>
            <option value="snack">Í∞ÑÏãù</option>
          </select>
        </div>

        {/* ÏùåÏãù Í≤ÄÏÉâ ÏÑπÏÖò */}
        <div className="section">
          <h3>ÏùåÏãù Ï∂îÍ∞ÄÌïòÍ∏∞</h3>
          <div>
            <label htmlFor="foodQuery">ÏùåÏãù Ïù¥Î¶Ñ Í≤ÄÏÉâ:</label>
            <div style={{ display: 'flex', gap: '10px' }}>
              <input
                type="text"
                id="foodQuery"
                value={currentFoodQuery}
                onChange={(e) => setCurrentFoodQuery(e.target.value)}
                placeholder="Ïòà: Îã≠Í∞ÄÏä¥ÏÇ¥"
              />
              <button type="button" onClick={handleFoodSearch} disabled={searchLoading}>
                {searchLoading ? 'Í≤ÄÏÉâ Ï§ë...' : 'ÏùåÏãù Í≤ÄÏÉâ'}
              </button>
            </div>
            {searchError && <p style={{ color: 'red', fontSize: '0.9em' }}>{searchError}</p>}
          </div>

          {searchedFoods.length > 0 && (
            <div>
              <h4>Í≤ÄÏÉâ Í≤∞Í≥º:</h4>
              <ul className="meal-items-list">
                {searchedFoods.map((food, index) => (
                  <li key={index}>
                    <span>{food.name} ({food.nutrition.kcal?.toFixed(0)} kcal) - Ïú†ÏÇ¨ÎèÑ: {food.score?.toFixed(2)}</span>
                    <button type="button" onClick={() => handleAddFoodToMeal(food)} style={{fontSize: '0.8em', padding: '3px 8px'}}>
                      Ï∂îÍ∞Ä
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          )}

          <button type="button" onClick={() => setShowManualAddForm(!showManualAddForm)} style={{marginTop: '10px', backgroundColor: '#5cb85c'}}>
            {showManualAddForm ? 'ÏßÅÏ†ë Ï∂îÍ∞Ä Ï∑®ÏÜå' : 'ÏõêÌïòÎäî ÏùåÏãùÏù¥ ÏóÜÎÇòÏöî? ÏßÅÏ†ë Ï∂îÍ∞Ä'}
          </button>
        </div>

        {/* ÏàòÎèô ÏûÖÎ†• Ìèº */} 
        {showManualAddForm && (
          <div className="section" style={{border: '1px dashed #3498db'}}>
            <h4>ÏùåÏãù ÏßÅÏ†ë Ï∂îÍ∞ÄÌïòÍ∏∞</h4>
            <div>
              <label htmlFor="manualFoodName">ÏùåÏãù Ïù¥Î¶Ñ:</label>
              <input type="text" id="manualFoodName" value={manualFoodName} onChange={(e) => setManualFoodName(e.target.value)} placeholder="Ïòà: ÏóÑÎßàÌëú ÍπÄÏπòÎ≥∂ÏùåÎ∞•" required={showManualAddForm} />
            </div>
            <div>
              <label htmlFor="manualKcal">ÏπºÎ°úÎ¶¨ (kcal):</label>
              <input type="number" id="manualKcal" value={manualKcal} onChange={(e) => setManualKcal(e.target.value)} placeholder="Ïòà: 550" required={showManualAddForm} />
            </div>
            <div><label htmlFor="manualProtein">Îã®Î∞±Ïßà (g):</label><input type="number" id="manualProtein" value={manualProtein} onChange={(e) => setManualProtein(e.target.value)} placeholder="(ÏÑ†ÌÉù)"/></div>
            <div><label htmlFor="manualCarbs">ÌÉÑÏàòÌôîÎ¨º (g):</label><input type="number" id="manualCarbs" value={manualCarbs} onChange={(e) => setManualCarbs(e.target.value)} placeholder="(ÏÑ†ÌÉù)"/></div>
            <div><label htmlFor="manualFat">ÏßÄÎ∞© (g):</label><input type="number" id="manualFat" value={manualFat} onChange={(e) => setManualFat(e.target.value)} placeholder="(ÏÑ†ÌÉù)"/></div>
            <button type="button" onClick={handleManualFoodAdd} style={{backgroundColor: '#f0ad4e'}}>
              ÏàòÎèôÏúºÎ°ú Ìï≠Î™© Ï∂îÍ∞Ä
            </button>
          </div>
        )}

        {/* ÌòÑÏû¨ ÏãùÏÇ¨Ïóê Ï∂îÍ∞ÄÎêú Ìï≠Î™© */} 
        {selectedItemsForMeal.length > 0 && (
          <div className="section">
            <h4>Ïù¥Î≤à ÏãùÏÇ¨Ïóê Ï∂îÍ∞ÄÎêú Ìï≠Î™©:</h4>
            <ul className="meal-items-list">
              {selectedItemsForMeal.map((item, index) => (
                <li key={index} style={{backgroundColor: item.source === 'manual' ? '#fff3cd' : '#e9f7ef'}}>
                  <span>
                    {item.name} 
                    (ÏïΩ {item.nutrition?.kcal?.toFixed(0) ?? 'N/A'} kcal
                    {item.source === 'manual' && ' - ÏßÅÏ†ëÏûÖÎ†•'})
                  </span>
                  <button type="button" onClick={() => handleRemoveFoodFromMeal(index)} style={{fontSize: '0.8em', padding: '3px 8px', backgroundColor: '#d9534f'}}>
                    Ï†úÍ±∞
                  </button>
                </li>
              ))}
            </ul>
          </div>
        )}

        <button type="submit" disabled={submitLoading || selectedItemsForMeal.length === 0} style={{marginTop: '20px', backgroundColor: '#0275d8'}}>
          {submitLoading ? 'ÏµúÏ¢Ö Í∏∞Î°ù Ï§ë...' : 'Ïù¥ ÏãùÎã®ÏúºÎ°ú ÏµúÏ¢Ö Í∏∞Î°ù'}
        </button>
      </form>
      {submitMessage && <p style={{ color: 'green' }}>{submitMessage}</p>}
      {submitError && <p style={{ color: 'red' }}>{submitError}</p>}
    </div>
  );
}

export default MealForm;
