import React from 'react';
import { deleteMeal } from '../services/api';

function SummaryDisplay({ summaryData, fetchSummary }) {
  if (!summaryData) {
    return <p className="loading">ÏöîÏïΩ Ï†ïÎ≥¥Î•º Î∂àÎü¨Ïò§Îäî Ï§ë...</p>;
  }

  const { goal, nutrition_total, remaining_kcal, meals, today_meals, message } = summaryData;

  const handleDeleteMeal = async (mealId, mealIndex) => {
    if (window.confirm('Ï†ïÎßêÎ°ú Ïù¥ ÏãùÏÇ¨Î•º ÏÇ≠Ï†úÌïòÏãúÍ≤†ÏäµÎãàÍπå?')) {
        try {
            // Î∞±ÏóîÎìúÏùò delete_mealÏùÄ DBÏùò idÍ∞Ä ÏïÑÎãå, ÌòÑÏû¨ meals Î¶¨Ïä§Ìä∏Ïùò Ïù∏Îç±Ïä§Î•º Í∏∞ÎåÄÌïòÍ≥† ÏûàÏäµÎãàÎã§.
            // App.jsÏóêÏÑú summaryData.mealsÎ•º Í¥ÄÎ¶¨ÌïòÎØÄÎ°ú, mealId(DBÏùò id) ÎåÄÏã† meals Î∞∞Ïó¥ÏóêÏÑúÏùò indexÎ•º Ï∞æÏïÑÏïº Ìï©ÎãàÎã§.
            // ÌïòÏßÄÎßå ÌòÑÏû¨ deleteMeal APIÎäî idx (ÏàúÏÑú)Î•º Î∞õÏúºÎØÄÎ°ú, Ìï¥Îãπ ÏãùÏÇ¨Ïùò ÏàúÏÑú(index)Î•º ÏÇ¨Ïö©Ìï©ÎãàÎã§.
            // Ï£ºÏùò: Ïù¥ Î∞©ÏãùÏùÄ meals Î∞∞Ïó¥Ïù¥ DBÏóêÏÑú Í∞ÄÏ†∏Ïò® ÏàúÏÑúÏôÄ ÎèôÏùºÌïòÎã§Í≥† Í∞ÄÏ†ïÌï©ÎãàÎã§.
            // ÎßåÏïΩ meal.id Í∞ôÏùÄ Í≥†Ïú† ÏãùÎ≥ÑÏûêÍ∞Ä ÏûàÎã§Î©¥ Í∑∏Í≤ÉÏùÑ ÏÇ¨Ïö©ÌïòÎäî Í≤ÉÏù¥ Îçî ÏïàÏ†ÑÌï©ÎãàÎã§.
            await deleteMeal(mealIndex); 
            alert('ÏãùÏÇ¨Í∞Ä ÏÇ≠Ï†úÎêòÏóàÏäµÎãàÎã§.');
            fetchSummary(); // ÏöîÏïΩ Ï†ïÎ≥¥ Îã§Ïãú Î∂àÎü¨Ïò§Í∏∞
        } catch (error) {
            alert('ÏãùÏÇ¨ ÏÇ≠Ï†ú Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
            console.error("Error deleting meal:", error);
        }
    }
  };

  const NutrientItem = ({ label, value, unit }) => (
    <p><strong>{label}:</strong> {value?.toFixed(1) ?? '0'} {unit}</p>
  );

  return (
    <div className="card">
      <h2 className="card-title">üìä Ïò§ÎäòÏùò ÏãùÏÇ¨ ÏöîÏïΩ</h2>
      {message && <p>{message}</p>}

      {goal && (
        <div className="section">
          <h3>üèÜ ÎÇòÏùò Î™©Ìëú</h3>
          <p><strong>ÌòÑÏû¨ Ï≤¥Ï§ë:</strong> {goal.current_weight} kg</p>
          <p><strong>Î™©Ìëú Ï≤¥Ï§ë:</strong> {goal.target_weight} kg</p>
          <p><strong>ÎÇ®ÏùÄ ÏπºÎ°úÎ¶¨:</strong> {remaining_kcal?.toFixed(1) ?? 'N/A'} kcal</p>
        </div>
      )}

      <div className="section">
        <h3>ü•ó Ïò§ÎäòÏùò Ï¥ù ÏÑ≠Ï∑® ÏòÅÏñë</h3>
        {nutrition_total ? (
          <div className="nutrient-info grid-container">
            <NutrientItem label="ÏπºÎ°úÎ¶¨" value={nutrition_total.kcal} unit="kcal" />
            <NutrientItem label="Îã®Î∞±Ïßà" value={nutrition_total.protein} unit="g" />
            <NutrientItem label="ÏßÄÎ∞©" value={nutrition_total.fat} unit="g" />
            <NutrientItem label="ÌÉÑÏàòÌôîÎ¨º" value={nutrition_total.carbs} unit="g" />
            <NutrientItem label="ÎÇòÌä∏Î•®" value={nutrition_total.sodium} unit="mg" />
            <NutrientItem label="ÏπºÎ•®" value={nutrition_total.potassium} unit="mg" />
            <NutrientItem label="Ïù∏" value={nutrition_total.phosphorus} unit="mg" />
          </div>
        ) : <p>Ïò§Îäò ÏÑ≠Ï∑®Ìïú ÏòÅÏñë Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.</p>}
      </div>

      <div className="section">
        <h3>üçΩÔ∏è Î™®Îì† ÏãùÏÇ¨ Í∏∞Î°ù</h3>
        {meals && meals.length > 0 ? (
          <ul className="meal-items-list">
            {meals.map((meal, index) => (
              <li key={meal.id || index}> {/* meal.idÍ∞Ä ÏûàÎã§Î©¥ ÏÇ¨Ïö©, ÏóÜÏúºÎ©¥ index ÏÇ¨Ïö© */}
                <span>
                  <strong>{meal.date} ({meal.type}):</strong> {meal.items.join(', ')}
                </span>
                {/* meal.idÍ∞Ä ÏûàÎã§Î©¥ Í∑∏Í≤ÉÏùÑ deleteMealÏùò Ïù∏ÏûêÎ°ú Ï†ÑÎã¨ÌïòÎäî Í≤ÉÏù¥ Ï¢ãÏäµÎãàÎã§. */}
                {/* ÌòÑÏû¨ APIÎäî indexÎ•º Î∞õÏúºÎØÄÎ°ú indexÎ•º ÏÇ¨Ïö©Ìï©ÎãàÎã§. */}
                <button onClick={() => handleDeleteMeal(meal.id, index)}>ÏÇ≠Ï†ú</button>
              </li>
            ))}
          </ul>
        ) : (
          <p>Í∏∞Î°ùÎêú ÏãùÏÇ¨Í∞Ä ÏóÜÏäµÎãàÎã§.</p>
        )}
      </div>
    </div>
  );
}

export default SummaryDisplay;
