import React, { useState, useEffect } from 'react';
import { getSnackRecommendations } from '../services/api';

function SnackRecommendations({ goal }) {
  const [snacks, setSnacks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchSnacks = async () => {
    if (!goal) { // Î™©ÌëúÍ∞Ä ÏÑ§Ï†ïÎêòÏñ¥Ïïº Í∞ÑÏãù Ï∂îÏ≤ú Í∞ÄÎä•
      setError('Í∞ÑÏãù Ï∂îÏ≤úÏùÑ Î∞õÏúºÎ†§Î©¥ Î®ºÏ†Ä Î™©ÌëúÎ•º ÏÑ§Ï†ïÌï¥Ï£ºÏÑ∏Ïöî.');
      setSnacks([]);
      return;
    }
    setLoading(true);
    setError('');
    try {
      const response = await getSnackRecommendations();
      setSnacks(response.data["Ï∂îÏ≤ú Í∞ÑÏãù"] || []);
    } catch (err) {
      setError(err.response?.data?.detail || 'Í∞ÑÏãù Ï∂îÏ≤úÏùÑ Î∂àÎü¨Ïò§Îäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.');
      console.error("Error fetching snacks:", err);
      setSnacks([]);
    } finally {
      setLoading(false);
    }
  };

  // Î™©ÌëúÍ∞Ä Î≥ÄÍ≤ΩÎê† ÎïåÎßàÎã§ Í∞ÑÏãù Ï∂îÏ≤ú Îã§Ïãú Î∂àÎü¨Ïò§Í∏∞ (ÏÑ†ÌÉùÏ†Å)
  // useEffect(() => {
  //   if (goal) fetchSnacks();
  // }, [goal]);

  return (
    <div className="card">
      <h2 className="card-title">üç™ Í±¥Í∞ï Í∞ÑÏãù Ï∂îÏ≤ú</h2>
      <button onClick={fetchSnacks} disabled={loading || !goal} style={{marginBottom: '15px'}}>
        {loading ? 'Ï∂îÏ≤ú Î°úÎî© Ï§ë...' : 'Í∞ÑÏãù Ï∂îÏ≤ú Î∞õÍ∏∞'}
      </button>
      {error && <p className="error">{error}</p>}
      {loading && !error && <p className="loading">Ï∂îÏ≤ú Í∞ÑÏãùÏùÑ Ï∞æÍ≥† ÏûàÏäµÎãàÎã§...</p>}
      {!loading && !error && snacks.length === 0 && goal && (
        <p>Ï∂îÏ≤úÌï† Í∞ÑÏãùÏù¥ ÏóÜÍ±∞ÎÇò, ÏïÑÏßÅ Ï∂îÏ≤úÏùÑ Î∞õÏßÄ ÏïäÏïòÏäµÎãàÎã§.</p>
      )}
      {!loading && !error && snacks.length > 0 && (
        <ul className="meal-items-list">
          {snacks.map((snack, index) => (
            <li key={index} className="snack-item">
              <strong>{snack["ÏãùÌíàÎ™Ö"]}</strong> - {snack["ÏóêÎÑàÏßÄ(kcal)"]?.toFixed(1)} kcal
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}

export default SnackRecommendations;
